<?php class contactController {	function initTable(){		$db = ORM::get_db();		$db->exec("			CREATE TABLE IF NOT EXISTS contact (				id INTEGER PRIMARY KEY, 				name TEXT, 				email TEXT 			);"		);		redirect('/contact/index');	}	function index() {		$contact = new Contact('contact');		var_dump($contact->count(), $contact->find(1));			$contact = ORM::for_table('contact')->create();	    $count = ORM::for_table('contact')->count();		$contact_list = ORM::for_table('contact')->find_many();		render('contact_index', array('count' => $count, 'contact_list' => $contact_list), 'layout');	}	function create() {		if (!empty($_POST)) {			$contact = new Contact('contact');		}		$this->save();		render('contact_create', null, 'layout');	}	function update() {		$this->save();		$id = params('id');		$contact = (new Contact)->find($id);		var_dump($contact->name);		render('contact_update', array('contact' => $contact), 'layout');	}	protected function save($redirect = '/contact/index') {		if (!empty($_POST)) {						// Create a new contact object			$contact = ORM::for_table('contact')->create();			// SHOULD BE MORE ERROR CHECKING HERE!			// Set the properties of the object			$contact->name = params('name');			$contact->email = params('email');			// Save the object to the database			$contact->save();						redirect($redirect);			exit;		}	}}