<?php class contactController {	function initTable(){		$db = ORM::get_db();		$db->exec("			CREATE TABLE IF NOT EXISTS contact (				id INTEGER PRIMARY KEY, 				name TEXT, 				email TEXT 			);"		);		redirect('/contact/index');	}	function index() {		$contact = new Contact();	    $count = $contact->count();		$contact_list = $contact->findAll();		render('contact_index', array('count' => $count, 'contact_list' => $contact_list), 'layout');	}	function create() {		if (!empty($_POST)) {			$contact = new Contact();			if ($contact->insert(array('name' => params('name'), 'email' => params('email'))))				redirect('/contact/index');		}		render('contact_create', null, 'layout');	}	function update() {		$id = params('id');		$contact = (new Contact)->find($id);		if (!empty($_POST)) {			if ($contact->update(array('name' => params('name'), 'email' => params('email'))))				redirect('/contact/index');		}		render('contact_update', array('contact' => $contact), 'layout');	}	public function delete() {		$id = params('id');		$contact = (new Contact)->find($id);		if ($contact->delete())			render('contact_delete', array('message' => "Delete Contact #$id SUCCESS."));		else render('contact_delete', array('message' => "Fail to delete Contact #$id."));	}}